//Lo mínimo para arrancar:
//npm i next react react-dom
//npm i -D @types/next @types/react @types/react-dom @zeit/next-typescript typescript
//creamos nuestros script en package.json
//creamos babel.config.js
//creamos next.config.js
//creamos tsconfig.json
import React, { useState, useRef } from "react";
import Container from "../components/container";

type FormElement = React.FormEvent<HTMLFormElement>;

interface iTask {
  name: string;
  done: boolean;
}

const Index = (): JSX.Element => {
  const [newTask, setNewTask] = useState<string>("");
  const [tasks, setTasks] = useState<iTask[]>([]);

  const inputRef = useRef<HTMLInputElement>(null);

  const handleSubmit = (e: FormElement) => {
    e.preventDefault();
    addTask(newTask);
    setNewTask("");
    inputRef.current?.focus();
  };
  //eL VOID va porque el addTask no tiene return!!!!!
  const addTask = (name: string): void => {
    const newTasks: iTask[] = [
      ...tasks,
      {
        name,
        done: false,
      },
    ];
    setTasks(newTasks);
    console.log(tasks);
  };

  const cambiarTask = (i: number): void => {
    const copyTask: iTask[] = [...tasks];
    copyTask[i].done = !copyTask[i].done;
    setTasks(copyTask);
  };

  const removeTask = (i: number): void => {
    const copyTask: iTask[] = [...tasks];
    copyTask.splice(i, 1);
    setTasks(copyTask);
  };

  return (
    <Container>
      <div className="row">
        <div className="col-md-6 offset-md-3">
          <div className="card">
            <div className="card-body">
              <form onSubmit={handleSubmit}>
                <input
                  type="text"
                  onChange={(e) => setNewTask(e.target.value)}
                  value={newTask}
                  className="form-control"
                  autoFocus
                  ref={inputRef}
                />
                <button className="btn btn-success btn-block mt-2">
                  Guardar
                </button>
              </form>
            </div>
          </div>
          {tasks.map((t: iTask, i: number) => (
            <div className="card card-body mt-2" key={i}>
              <h2 style={{ textDecoration: t.done ? "line-through" : "" }}>
                {t.name}
              </h2>
              <p style={{ float: "left" }}>{t.done + ""}</p>
              <div>
                <button
                  className="btn btn-secondary"
                  onClick={() => cambiarTask(i)}
                >
                  {t.done ? "✓" : "x"}
                </button>
                <br />
                <button
                  className="btn btn-danger"
                  onClick={() => removeTask(i)}
                >
                  Eliminar
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </Container>
  );
};

export default Index;
